PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;


CREATE TABLE IF NOT EXISTS "blog_post" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(200) NOT NULL UNIQUE, "slug" varchar(200) NOT NULL UNIQUE, "updated_on" datetime NOT NULL, "content" text NOT NULL, "created_on" datetime NULL, "status" integer NOT NULL, "author_id" integer NOT NULL REFERENCES "auth_user" ("id") DEFERRABLE INITIALLY DEFERRED, "preview" varchar(200) NOT NULL);

CREATE TABLE IF NOT EXISTS "blog_project" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(200) NOT NULL UNIQUE, "slug" varchar(200) NOT NULL UNIQUE, "updated_on" datetime NOT NULL, "content" text NULL, "status" integer NOT NULL, "completed_on" datetime NULL, "github_link" varchar(600) NULL, "image_url1" varchar(200) NULL, "image_url2" varchar(200) NULL, "image_url3" varchar(200) NULL, "image_url4" varchar(200) NULL, "image_url5" varchar(200) NULL, "image_url6" varchar(200) NULL, "live_link" varchar(600) NULL, "other_link" varchar(600) NULL, "other_link_title" varchar(600) NULL, "preview" varchar(600) NULL, "video_url1" varchar(200) NULL, "video_url2" varchar(200) NULL, "video_url3" varchar(200) NULL, "video_url4" varchar(200) NULL, "priority_order" integer NOT NULL);

CREATE TABLE IF NOT EXISTS "blog_book" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "title" varchar(200) NOT NULL UNIQUE, "slug" varchar(200) NOT NULL UNIQUE, "image_url" varchar(200) NOT NULL, "author" varchar(200) NOT NULL, "updated_on" datetime NOT NULL, "read_on" date NULL, "content" text NOT NULL, "preview" varchar(200) NOT NULL, "created_on" datetime NOT NULL, "status" integer NOT NULL, "book_status" integer NOT NULL, "book_format" integer NOT NULL);

COMMIT;
