INSERT INTO quotesApi_quote VALUES(73,'Richard Evans',NULL,5,'GENERAL','The undertaking of a new action brings new strength.');
INSERT INTO quotesApi_quote VALUES(74,'Richard Bach',NULL,5,'GENERAL','Allow the world to live as it chooses, and allow yourself to live as you choose.');
